import React, { Component } from 'react';

class Header extends Component {
    constructor() {
        super();
        this.state = {
            homeVideoStyle: {
                opacity: '0'
            },
            homeImgStyle: {
                opacity: '1'
            }
        }
    }
    componentDidMount() {

    }
    showVideo = () => {

    }
    init = () => {
        this.setState({
            homeVideoStyle: {
                opacity: '1'
            },
            homeImgStyle: {
                opacity: '0'
            }
        });
    }
    render() {
        return (
            <section className="header" style={{ width: '100%', height: window.screen.height / 2 + "px" }}>
                {/* <video style={{display:this.state.homeVideoDisplay}}ref={'homeVideo'} onLoadStart={this.showHomeVideo} className="banner-video" src="../src/static/ball.mp4" type="video/mp4" autoPlay muted loop width="100%" height={window.screen.height / 2+"px"}/> */}
                <video style={this.state.homeVideoStyle} ref={'homeVideo'} onLoadStart={this.init} className="banner-video" src={require('../static/ball.mp4')} type="video/mp4" autoPlay muted loop width="100%" height={window.screen.height / 2 + "px"} />
                <img style={this.state.homeImgStyle} ref={'homeImg'} className="banner-image" src={require('../static/homeImg.png')} width="100%" height={window.screen.height / 2 + "px"} alt="" />
                {/* <div className="go-bottom-button">往下</div> */}
            </section>
        );
    }
}

export default Header;
